// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {CheckpointsUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/CheckpointsUpgradeable.sol";

import {_uncheckedAdd, _uncheckedSub} from "@aragon/osx/utils/UncheckedMath.sol";

// Based on https://github.com/aragon/osx/blob/develop/packages/contracts/src/plugins/utils/Addresslist.sol
abstract contract TokenList {
    using CheckpointsUpgradeable for CheckpointsUpgradeable.History;

    event TokensAdded(uint256[] tokens);
    event TokensRemoved(uint256[] tokens);

    /// @notice The mapping containing the checkpointed history of the token list.
    mapping(uint256 => CheckpointsUpgradeable.History)
        private _tokenlistCheckpoints;

    /// @notice The checkpointed history of the length of the address list.
    CheckpointsUpgradeable.History private _tokenlistLengthCheckpoints;

    /// @notice Thrown when the token list update is invalid, which can be caused by the addition of an existing tokenId or removal of a non-existing tokenId.
    /// @param tokenId The first invalid tokenId.
    error InvalidTokenlistUpdate(uint256 tokenId);

    /// @notice Checks if a tokenId is on the address list at a specific block number.
    /// @param _tokenId The tokenId being checked.
    /// @param _blockNumber The block number.
    /// @return Whether the tokenId is listed at the specified block number.
    function isListedAtBlock(
        uint256 _tokenId,
        uint256 _blockNumber
    ) public view virtual returns (bool) {
        return _tokenlistCheckpoints[_tokenId].getAtBlock(_blockNumber) == 1;
    }

    /// @notice Checks if a tokenId is currently on the address list.
    /// @param _tokenId The tokenId being checked.
    /// @return Whether the tokenId is currently listed.
    function isListed(uint256 _tokenId) public view virtual returns (bool) {
        return _tokenlistCheckpoints[_tokenId].latest() == 1;
    }

    /// @notice Returns the length of the token list at a specific block number.
    /// @param _blockNumber The specific block to get the count from. If `0`, then the latest checkpoint value is returned.
    /// @return The token list length at the specified block number.
    function tokenlistLengthAtBlock(
        uint256 _blockNumber
    ) public view virtual returns (uint256) {
        return _tokenlistLengthCheckpoints.getAtBlock(_blockNumber);
    }

    /// @notice Returns the current length of the token list.
    /// @return The current token list length.
    function tokenlistLength() public view virtual returns (uint256) {
        return _tokenlistLengthCheckpoints.latest();
    }

    /// @notice Internal function to add new tokens to the token list.
    /// @param _newTokens The new tokens to be added.
    function _addTokens(uint256[] calldata _newTokens) internal virtual {
        for (uint256 i; i < _newTokens.length; ) {
            if (isListed(_newTokens[i])) {
                revert InvalidTokenlistUpdate(_newTokens[i]);
            }

            // Mark the address as listed
            _tokenlistCheckpoints[_newTokens[i]].push(1);

            unchecked {
                ++i;
            }
        }
        _tokenlistLengthCheckpoints.push(_uncheckedAdd, _newTokens.length);

        emit TokensAdded(_newTokens);
    }

    /// @notice Internal function to remove existing tokens from the token list.
    /// @param _exitingTokens The existing tokens to be removed.
    function _removeTokens(uint256[] calldata _exitingTokens) internal virtual {
        for (uint256 i; i < _exitingTokens.length; ) {
            if (!isListed(_exitingTokens[i])) {
                revert InvalidTokenlistUpdate(_exitingTokens[i]);
            }

            // Mark the address as not listed
            _tokenlistCheckpoints[_exitingTokens[i]].push(0);

            unchecked {
                ++i;
            }
        }
        _tokenlistLengthCheckpoints.push(_uncheckedSub, _exitingTokens.length);

        emit TokensRemoved(_exitingTokens);
    }

    /// @dev This empty reserved space is put in place to allow future versions to add new
    /// variables without shifting down storage in the inheritance chain.
    /// https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
    uint256[50] private __gap;
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {SafeCastUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/math/SafeCastUpgradeable.sol";
import {RATIO_BASE, _applyRatioCeiled} from "@aragon/osx/plugins/utils/Ratio.sol";
import {TokenList} from "./TokenList.sol";

import {TokenMajorityVotingBase, IDAO, IERC721} from "../TokenMajorityVoting/TokenMajorityVotingBase.sol";
import {ITokenMembership} from "../TokenMembership/ITokenMembership.sol";
import {ITokenListGovernance} from "./ITokenListGovernance.sol";
import {IPluginProposals, PLUGIN_PROPOSAL_PERMISSION_ID} from "./IPluginProposals.sol";

// Based on https://github.com/aragon/osx/blob/develop/packages/contracts/src/plugins/governance/majority-voting/addresslist/AddresslistVoting.sol
contract TokenListGovernance is
    TokenMajorityVotingBase,
    TokenList,
    ITokenMembership,
    ITokenListGovernance,
    IPluginProposals
{
    using SafeCastUpgradeable for uint256;

    /// @notice The ID of the permission required to call the `addMembers` and `removeMembers` functions.
    bytes32 public constant UPDATE_MEMBERS_PERMISSION_ID =
        keccak256("UPDATE_MEMBERS_PERMISSION");

    /// @notice Initializes the component.
    /// @dev This method is required to support [ERC-1822](https://eips.ethereum.org/EIPS/eip-1822).
    /// @param _dao The IDAO interface of the associated DAO.
    /// @param _votingSettings The voting settings.
    /// @param _tokenCollection The ERC721 contract that defines who has what token.
    /// @param _members Initial members of the DAO.
    function initialize(
        IDAO _dao,
        VotingSettings calldata _votingSettings,
        IERC721 _tokenCollection,
        uint256[] calldata _members
    ) external initializer {
        __TokenMajorityVotingBase_init(_dao, _votingSettings, _tokenCollection);

        _addTokens(_members);
    }

    /// @notice Checks if this or the parent contract supports an interface by its ID.
    /// @param _interfaceId The ID of the interface.
    /// @return Returns `true` if the interface is supported.
    function supportsInterface(
        bytes4 _interfaceId
    ) public view virtual override returns (bool) {
        return
            _interfaceId == type(ITokenListGovernance).interfaceId ||
            super.supportsInterface(_interfaceId);
    }

    /// @inheritdoc ITokenListGovernance
    function addMembers(
        uint256[] calldata _members
    ) external auth(UPDATE_MEMBERS_PERMISSION_ID) {
        _addTokens(_members);
    }

    /// @inheritdoc ITokenListGovernance
    function removeMembers(
        uint256[] calldata _members
    ) external auth(UPDATE_MEMBERS_PERMISSION_ID) {
        _removeTokens(_members);
    }

    /// @inheritdoc TokenMajorityVotingBase
    function totalVotingPower(
        uint256 _blockNumber
    ) public view override returns (uint256) {
        return tokenlistLengthAtBlock(_blockNumber);
    }

    /// @inheritdoc IPluginProposals
    function createPluginProposal(
        bytes calldata _metadata,
        IDAO.Action[] calldata _actions,
        uint256 _allowFailureMap,
        uint64 _startDate,
        uint64 _endDate
    )
        external
        auth(PLUGIN_PROPOSAL_PERMISSION_ID)
        returns (uint256 proposalId)
    {
        proposalId = _createProposalBase(
            _metadata,
            _actions,
            _allowFailureMap,
            _startDate,
            _endDate
        );
    }

    /// @inheritdoc TokenMajorityVotingBase
    function createProposal(
        bytes calldata _metadata,
        IDAO.Action[] calldata _actions,
        uint256 _allowFailureMap,
        uint64 _startDate,
        uint64 _endDate,
        VoteOption _voteOption,
        bool _tryEarlyExecution,
        uint256 _tokenId
    ) external override returns (uint256 proposalId) {
        if (tokenCollection.ownerOf(_tokenId) != _msgSender()) {
            revert TokenNotOwnedBySender(_tokenId, _msgSender());
        }

        if (minProposerVotingPower() != 0 && !isListed(_tokenId)) {
            revert ProposalCreationForbidden(_tokenId);
        }

        proposalId = _createProposalBase(
            _metadata,
            _actions,
            _allowFailureMap,
            _startDate,
            _endDate
        );

        if (_voteOption != VoteOption.None) {
            vote(proposalId, _voteOption, _tryEarlyExecution, _tokenId);
        }
    }

    /// @inheritdoc ITokenMembership
    function isMember(uint256 _account) external view override returns (bool) {
        return isListed(_account);
    }

    function _createProposalBase(
        bytes calldata _metadata,
        IDAO.Action[] calldata _actions,
        uint256 _allowFailureMap,
        uint64 _startDate,
        uint64 _endDate
    ) internal returns (uint256 proposalId) {
        uint64 snapshotBlock;
        unchecked {
            snapshotBlock = block.number.toUint64() - 1; // The snapshot block must be mined already to protect the transaction against backrunning transactions causing census changes.
        }

        (_startDate, _endDate) = _validateProposalDates(_startDate, _endDate);

        proposalId = _createProposal({
            _creator: _msgSender(),
            _metadata: _metadata,
            _startDate: _startDate,
            _endDate: _endDate,
            _actions: _actions,
            _allowFailureMap: _allowFailureMap
        });

        // Store proposal related information
        Proposal storage proposal_ = proposals[proposalId];

        proposal_.parameters.startDate = _startDate;
        proposal_.parameters.endDate = _endDate;
        proposal_.parameters.snapshotBlock = snapshotBlock;
        proposal_.parameters.votingMode = votingMode();
        proposal_.parameters.supportThreshold = supportThreshold();
        proposal_.parameters.minVotingPower = _applyRatioCeiled(
            totalVotingPower(snapshotBlock),
            minParticipation()
        );

        // Reduce costs
        if (_allowFailureMap != 0) {
            proposal_.allowFailureMap = _allowFailureMap;
        }

        for (uint256 i; i < _actions.length; ) {
            proposal_.actions.push(_actions[i]);
            unchecked {
                ++i;
            }
        }
    }

    /// @inheritdoc TokenMajorityVotingBase
    function _vote(
        uint256 _proposalId,
        VoteOption _voteOption,
        uint256 _voter,
        bool _tryEarlyExecution
    ) internal override {
        Proposal storage proposal_ = proposals[_proposalId];

        VoteOption state = proposal_.voters[_voter];

        // Remove the previous vote.
        if (state == VoteOption.Yes) {
            proposal_.tally.yes = proposal_.tally.yes - 1;
        } else if (state == VoteOption.No) {
            proposal_.tally.no = proposal_.tally.no - 1;
        } else if (state == VoteOption.Abstain) {
            proposal_.tally.abstain = proposal_.tally.abstain - 1;
        }

        // Store the updated/new vote for the voter.
        if (_voteOption == VoteOption.Yes) {
            proposal_.tally.yes = proposal_.tally.yes + 1;
        } else if (_voteOption == VoteOption.No) {
            proposal_.tally.no = proposal_.tally.no + 1;
        } else if (_voteOption == VoteOption.Abstain) {
            proposal_.tally.abstain = proposal_.tally.abstain + 1;
        }

        proposal_.voters[_voter] = _voteOption;

        emit VoteCast({
            proposalId: _proposalId,
            voter: _voter,
            voteOption: _voteOption,
            votingPower: 1
        });

        if (_tryEarlyExecution && _canExecute(_proposalId)) {
            _execute(_proposalId);
        }
    }

    /// @inheritdoc TokenMajorityVotingBase
    function _canVote(
        uint256 _proposalId,
        uint256 _account,
        VoteOption _voteOption
    ) internal view override returns (bool) {
        Proposal storage proposal_ = proposals[_proposalId];

        // The proposal vote hasn't started or has already ended.
        if (!_isProposalOpen(proposal_)) {
            return false;
        }

        // The voter votes `None` which is not allowed.
        if (_voteOption == VoteOption.None) {
            return false;
        }

        // The voter has no voting power.
        if (!isListedAtBlock(_account, proposal_.parameters.snapshotBlock)) {
            return false;
        }

        // The voter has already voted but vote replacement is not allowed.
        if (
            proposal_.voters[_account] != VoteOption.None &&
            proposal_.parameters.votingMode != VotingMode.VoteReplacement
        ) {
            return false;
        }

        return true;
    }

    /// @dev This empty reserved space is put in place to allow future versions to add new
    /// variables without shifting down storage in the inheritance chain.
    /// https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
    uint256[50] private __gap;
}

contracts/DAO/TokenMajorityVoting/IToken.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {IDAO} from "@aragon/osx/core/dao/IDAO.sol";

interface ITokenMajorityVoting {
    /// @notice Vote options that a voter can chose from.
    /// @param None The default option state of a voter indicating the absence from the vote. This option neither influences support nor participation.
    /// @param Abstain This option does not influence the support but counts towards participation.
    /// @param Yes This option increases the support and counts towards participation.
    /// @param No This option decreases the support and counts towards participation.
    enum VoteOption {
        None,
        Abstain,
        Yes,
        No
    }

    /// @notice Emitted when a vote is cast by a voter.
    /// @param proposalId The ID of the proposal.
    /// @param voter The voter casting the vote.
    /// @param voteOption The casted vote option.
    /// @param votingPower The voting power behind this vote.
    event VoteCast(
        uint256 indexed proposalId,
        uint256 indexed voter,
        VoteOption voteOption,
        uint256 votingPower
    );

    /// @notice Returns the support threshold parameter stored in the voting settings.
    /// @return The support threshold parameter.
    function supportThreshold() external view returns (uint32);

    /// @notice Returns the minimum participation parameter stored in the voting settings.
    /// @return The minimum participation parameter.
    function minParticipation() external view returns (uint32);

    /// @notice Checks if the support value defined as $$\texttt{support} = \frac{N_\text{yes}}{N_\text{yes}+N_\text{no}}$$ for a proposal vote is greater than the support threshold.
    /// @param _proposalId The ID of the proposal.
    /// @return Returns `true` if the  support is greater than the support threshold and `false` otherwise.
    function isSupportThresholdReached(
        uint256 _proposalId
    ) external view returns (bool);

    /// @notice Checks if the worst-case support value defined as $$\texttt{worstCaseSupport} = \frac{N_\text{yes}}{ N_\text{total}-N_\text{abstain}}$$ for a proposal vote is greater than the support threshold.
    /// @param _proposalId The ID of the proposal.
    /// @return Returns `true` if the worst-case support is greater than the support threshold and `false` otherwise.
    function isSupportThresholdReachedEarly(
        uint256 _proposalId
    ) external view returns (bool);

    /// @notice Checks if the participation value defined as $$\texttt{participation} = \frac{N_\text{yes}+N_\text{no}+N_\text{abstain}}{N_\text{total}}$$ for a proposal vote is greater or equal than the minimum participation value.
    /// @param _proposalId The ID of the proposal.
    /// @return Returns `true` if the participation is greater than the minimum participation and `false` otherwise.
    function isMinParticipationReached(
        uint256 _proposalId
    ) external view returns (bool);

    /// @notice Checks if an account can participate on a proposal vote. This can be because the vote
    /// - has not started,
    /// - has ended,
    /// - was executed, or
    /// - the voter doesn't have voting powers.
    /// @param _proposalId The proposal Id.
    /// @param _account The account tokenId to be checked.
    /// @param  _voteOption Whether the voter abstains, supports or opposes the proposal.
    /// @return Returns true if the account is allowed to vote.
    /// @dev The function assumes the queried proposal exists.
    function canVote(
        uint256 _proposalId,
        uint256 _account,
        VoteOption _voteOption
    ) external view returns (bool);

    /// @notice Checks if a proposal can be executed.
    /// @param _proposalId The ID of the proposal to be checked.
    /// @return True if the proposal can be executed, false otherwise.
    function canExecute(uint256 _proposalId) external view returns (bool);

    /// @notice Votes for a vote option and, optionally, executes the proposal.
    /// @dev `_voteOption`, 1 -> abstain, 2 -> yes, 3 -> no
    /// @param _proposalId The ID of the proposal.
    /// @param _voteOption The chosen vote option.
    /// @param _tryEarlyExecution If `true`,  early execution is tried after the vote cast. The call does not revert if early execution is not possible.
    /// @param _tokenId TokenId to use for this vote.
    function vote(
        uint256 _proposalId,
        VoteOption _voteOption,
        bool _tryEarlyExecution,
        uint256 _tokenId
    ) external;

    /// @notice Executes a proposal.
    /// @param _proposalId The ID of the proposal to be executed.
    function execute(uint256 _proposalId) external;

    /// @notice Returns whether the account has voted for the proposal.  Note, that this does not check if the account has voting power.
    /// @param _proposalId The ID of the proposal.
    /// @param _account The account tokenId to be checked.
    /// @return The vote option cast by a voter for a certain proposal.
    function getVoteOption(
        uint256 _proposalId,
        uint256 _account
    ) external view returns (VoteOption);
}

