// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Escrow} from "../Escrow.sol";
import {ITasks} from "../Tasks/ITasks.sol";

interface IRFPs {
    error RFPDoesNotExist();
    error RFPClosed();
    error NotManager();
    error ProjectDoesNotExist();
    error ProjectAlreadyAccepted();
    error RewardDoesntEndWithNextToken();

    error Overflow();
    error ERC1167FailedCreateClone();

    event RFPCreated(
        uint256 indexed rfpId,
        string metadata,
        uint64 deadline,
        ITasks.ERC20Transfer[] budget,
        uint96 nativeBudget,
        address creator,
        address tasksManager,
        address manager
    );
    event ProjectSubmitted(
        uint256 indexed rfpId,
        uint16 projectId,
        string metadata,
        address representative,
        uint64 deadline,
        ITasks.Reward[] reward,
        ITasks.NativeReward[] nativeReward
    );
    event ProjectAccepted(
        uint256 indexed rfpId,
        uint16 projectId,
        uint256 taskId
    );
    event RFPEmptied(uint256 indexed rfpId);

    /// @notice A container for a RFP project.
    /// @param metadata Metadata of the project. (IPFS hash)
    /// @param representative Who has submitted this project.
    /// @param deadline The deadline after which the project should be completed.
    /// @param accepted If the project has been accepted. To prevent 2 OpenR&D tasks from being created.
    /// @param reward How much rewards the representative wants for completion.
    /// @param nativeReward How much native currency the representative wants for completion.
    struct Project {
        string metadata;
        address representative;
        uint64 deadline;
        bool accepted;
        uint8 rewardCount;
        uint8 nativeRewardCount;
        mapping(uint8 => ITasks.Reward) reward;
        mapping(uint8 => ITasks.NativeReward) nativeReward;
    }

    struct OffchainProject {
        string metadata;
        address representative;
        uint64 deadline;
        bool accepted;
        ITasks.Reward[] reward;
        ITasks.NativeReward[] nativeReward;
    }

    /// @notice A container for RFP-related information.
    /// @param metadata Metadata of the RFP. (IPFS hash)
    /// @param deadline Block timestamp at which the RFP closes.
    /// @param budget The ERC20 contract that compose the budget.
    /// @param nativeBudget Maximum native currency reward available for projects of the RFP.
    /// @param creator Who has created the RFP.
    /// @param tasksManager Who has the permission to manage the OpenR&D tasks.
    /// @param manager Who has the permission to manage the RFP.
    /// @param projects Projects that want to be funded by the RFP.
    struct RFP {
        string metadata;
        // Storage block seperator
        uint64 deadline;
        Escrow escrow;
        // Storage block seperator
        uint96 nativeBudget;
        address creator;
        // Storage block seperator
        address tasksManager;
        // Storage block seperator
        address manager;
        uint8 budgetCount;
        uint16 projectCount;
        // Storage block seperator
        mapping(uint8 => IERC20) budget;
        mapping(uint16 => Project) projects;
    }

    struct OffChainRFP {
        string metadata;
        uint64 deadline;
        Escrow escrow;
        uint96 nativeBudget;
        address creator;
        address tasksManager;
        address manager;
        IERC20[] budget;
        OffchainProject[] projects;
    }

    /// @notice Retrieves the current amount of created RFPs.
    function rfpCount() external view returns (uint256);

    /// @notice Retrieves all RFP information by id.
    /// @param _rfpId Id of the RFP.
    function getRFP(uint256 _rfpId) external view returns (OffChainRFP memory);

    /// @notice Retrieves multiple RFPs.
    /// @param _rfpIds Ids of the RFPs.
    function getRFPs(
        uint256[] calldata _rfpIds
    ) external view returns (OffChainRFP[] memory);

    /// @notice Create a new RFP.
    /// @param _metadata Metadata of the RFP. (IPFS hash)
    /// @param _deadline Block timestamp at which the RFP closes.
    /// @param _budget Maximum ERC20 rewards avaliable for projects of the RFP.
    /// @param _tasksManager Who will manage the project Tasks (become the OpenR&D manager).
    /// @param _manager Who will manage the RFP (become the manager).
    /// @return rfpId Id of the newly created RFP.
    function createRFP(
        string calldata _metadata,
        uint64 _deadline,
        ITasks.ERC20Transfer[] calldata _budget,
        address _tasksManager,
        address _manager
    ) external payable returns (uint256 rfpId);

    /// @notice Propose a project to be funded by an RFP.
    /// @param _rfpId Id of the RFP.
    /// @param _metadata Metadata of your project.
    /// @param _deadline Before when the proposed project will be completed.
    /// @param _reward Wanted rewards from the RFP for the project.
    /// @param _nativeReward Wanted native currency from the RFP for the project.
    function submitProject(
        uint256 _rfpId,
        string calldata _metadata,
        uint64 _deadline,
        ITasks.Reward[] calldata _reward,
        ITasks.NativeReward[] calldata _nativeReward
    ) external returns (uint16 projectId);

    /// @notice Accept project to be funnded by the RFP.
    /// @param _rfpId Id of the RFP.
    /// @param _projectId Id of the project to accept.
    function acceptProject(uint256 _rfpId, uint16 _projectId) external;

    /// @notice Refunds any leftover budget to the creator.
    /// @param _rfpId Id of the RFP.
    function emptyRFP(uint256 _rfpId) external;
}

contracts/RFP/RFPs.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {IRFPs, IERC20, Escrow, ITasks} from "./IRFPs.sol";

contract RFPs is IRFPs {
    /// @notice The Tasks deployment where accepted projects will be created.
    ITasks private tasks;

    /// @notice The incremental ID for RFPs.
    uint256 private rfpCounter;

    /// @notice A mapping between RFP IDs and RFP information.
    mapping(uint256 => RFP) internal rfps;

    /// @notice The base escrow contract that will be cloned for every RFP.
    address public immutable escrowImplementation;

    constructor(ITasks _tasks) {
        tasks = _tasks;
        escrowImplementation = address(new Escrow());
    }

    receive() external payable {}

    /// @inheritdoc IRFPs
    function rfpCount() external view returns (uint256) {
        return rfpCounter;
    }

    /// @inheritdoc IRFPs
    function getRFP(
        uint256 _rfpId
    ) public view returns (OffChainRFP memory offchainRFP) {
        RFP storage rfp = _getRFP(_rfpId);
        offchainRFP = _toOffchainRFP(rfp);
    }

    /// @inheritdoc IRFPs
    function getRFPs(
        uint256[] memory _rfpIds
    ) public view returns (OffChainRFP[] memory) {
        OffChainRFP[] memory offchainRFPs = new OffChainRFP[](_rfpIds.length);
        for (uint i; i < _rfpIds.length; ) {
            offchainRFPs[i] = getRFP(_rfpIds[i]);

            unchecked {
                ++i;
            }
        }
        return offchainRFPs;
    }

    /// @inheritdoc IRFPs
    function createRFP(
        string calldata _metadata,
        uint64 _deadline,
        ITasks.ERC20Transfer[] calldata _budget,
        address _tasksManager,
        address _manager
    ) external payable returns (uint256 rfpId) {
        rfpId = rfpCounter++;

        RFP storage rfp = rfps[rfpId];
        rfp.metadata = _metadata;
        rfp.deadline = _deadline;
        Escrow escrow = Escrow(payable(clone(escrowImplementation)));
        escrow.__Escrow_init{value: msg.value}();
        rfp.escrow = escrow;
        // Gas optimization
        if (msg.value != 0) {
            rfp.nativeBudget = _toUint96(msg.value);
        }
        rfp.budgetCount = _toUint8(_budget.length);
        for (uint8 i; i < uint8(_budget.length); ) {
            _budget[i].tokenContract.transferFrom(
                msg.sender,
                address(escrow),
                _budget[i].amount
            );
            // use balanceOf in case there is a fee asoosiated with the transfer
            rfp.budget[i] = _budget[i].tokenContract;
            unchecked {
                ++i;
            }
        }

        rfp.manager = _manager;
        rfp.tasksManager = _tasksManager;
        rfp.creator = msg.sender;

        emit RFPCreated(
            rfpId,
            _metadata,
            _deadline,
            _budget,
            _toUint96(msg.value),
            msg.sender,
            _tasksManager,
            _manager
        );
    }

    /// @inheritdoc IRFPs
    function submitProject(
        uint256 _rfpId,
        string calldata _metadata,
        uint64 _deadline,
        ITasks.Reward[] calldata _reward,
        ITasks.NativeReward[] calldata _nativeReward
    ) external returns (uint16 projectId) {
        RFP storage rfp = _getRFP(_rfpId);

        // Ensure not past deadline
        if (rfp.deadline <= uint64(block.timestamp)) {
            revert RFPClosed();
        }
        // Ensure reward ends with next token
        unchecked {
            if (_reward.length != 0 && !_reward[_reward.length - 1].nextToken) {
                revert RewardDoesntEndWithNextToken();
            }
        }

        Project storage project = rfp.projects[rfp.projectCount];
        project.metadata = _metadata;
        project.deadline = _deadline;
        project.representative = msg.sender;

        // Gas optimization
        if (_reward.length != 0) {
            project.rewardCount = _toUint8(_reward.length);
            for (uint8 i; i < uint8(_reward.length); ) {
                project.reward[i] = _reward[i];
                unchecked {
                    ++i;
                }
            }
        }

        // Gas optimization
        if (_nativeReward.length != 0) {
            project.nativeRewardCount = _toUint8(_nativeReward.length);
            for (uint8 i; i < uint8(_nativeReward.length); ) {
                project.nativeReward[i] = _nativeReward[i];
                unchecked {
                    ++i;
                }
            }
        }

        projectId = rfp.projectCount++;

        emit ProjectSubmitted(
            _rfpId,
            projectId,
            _metadata,
            msg.sender,
            _deadline,
            _reward,
            _nativeReward
        );
    }

    /// @inheritdoc IRFPs
    function acceptProject(uint256 _rfpId, uint16 _projectId) external {
        RFP storage rfp = _getRFP(_rfpId);

        // Ensure sender is manager
        if (rfp.manager != msg.sender) {
            revert NotManager();
        }
        // Ensure project exists
        if (_projectId >= rfp.projectCount) {
            revert ProjectDoesNotExist();
        }

        Project storage project = rfp.projects[_projectId];

        // Ensure project not accepted
        if (project.accepted) {
            revert ProjectAlreadyAccepted();
        }

        ITasks.ERC20Transfer[] memory taskBudget = new ITasks.ERC20Transfer[](
            rfp.budgetCount
        );
        ITasks.Reward[] memory taskReward = new ITasks.Reward[](
            project.rewardCount
        );
        uint8 j;
        for (uint8 i; i < taskBudget.length; ) {
            IERC20 erc20 = rfp.budget[i];
            uint96 projectBudget;
            while (j < taskReward.length) {
                taskReward[j] = project.reward[j];
                projectBudget += taskReward[j].amount;

                unchecked {
                    ++j;
                }

                if (taskReward[j - 1].nextToken) {
                    break;
                }
            }

            taskBudget[i] = ITasks.ERC20Transfer(erc20, projectBudget);
            rfp.escrow.transfer(erc20, address(this), projectBudget);
            erc20.approve(address(tasks), projectBudget);

            unchecked {
                ++i;
            }
        }

        uint96 taskNativeBudget;
        ITasks.NativeReward[]
            memory taskNativeReward = new ITasks.NativeReward[](
                project.nativeRewardCount
            );
        for (uint8 i; i < taskNativeReward.length; ) {
            taskNativeReward[i] = project.nativeReward[i];
            taskNativeBudget += taskNativeReward[i].amount;

            unchecked {
                ++i;
            }
        }
        rfp.escrow.transferNative(payable(address(this)), taskNativeBudget);

        ITasks.PreapprovedApplication[]
            memory preapproved = new ITasks.PreapprovedApplication[](1);
        preapproved[0] = ITasks.PreapprovedApplication(
            project.representative,
            taskReward,
            taskNativeReward
        );

        uint256 taskId = tasks.createTask{value: taskNativeBudget}(
            project.metadata,
            project.deadline,
            taskBudget,
            rfp.tasksManager,
            preapproved
        );
        project.accepted = true;
        emit ProjectAccepted(_rfpId, _projectId, taskId);
    }

    /// @inheritdoc IRFPs
    function emptyRFP(uint256 _rfpId) external {
        RFP storage rfp = _getRFP(_rfpId);

        // Ensure sender is manager
        if (rfp.manager != msg.sender) {
            revert NotManager();
        }

        uint8 budgetCount = rfp.budgetCount;
        for (uint8 i; i < budgetCount; ) {
            IERC20 erc20 = rfp.budget[i];

            rfp.escrow.transfer(
                erc20,
                rfp.creator,
                erc20.balanceOf(address(rfp.escrow))
            );

            unchecked {
                ++i;
            }
        }

        rfp.escrow.transferNative(
            payable(rfp.creator),
            address(rfp.escrow).balance
        );

        emit RFPEmptied(_rfpId);
    }

    function _getRFP(uint256 _rfpId) internal view returns (RFP storage rfp) {
        if (_rfpId >= rfpCounter) {
            revert RFPDoesNotExist();
        }

        rfp = rfps[_rfpId];
    }

    function _toOffchainRFP(
        RFP storage rfp
    ) internal view returns (OffChainRFP memory offchainRFP) {
        offchainRFP.metadata = rfp.metadata;
        offchainRFP.deadline = rfp.deadline;
        offchainRFP.creator = rfp.creator;
        offchainRFP.tasksManager = rfp.tasksManager;
        offchainRFP.manager = rfp.manager;
        offchainRFP.escrow = rfp.escrow;
        offchainRFP.nativeBudget = rfp.nativeBudget;

        offchainRFP.budget = new IERC20[](rfp.budgetCount);
        for (uint8 i; i < offchainRFP.budget.length; ) {
            offchainRFP.budget[i] = rfp.budget[i];
            unchecked {
                ++i;
            }
        }

        offchainRFP.projects = new OffchainProject[](rfp.projectCount);
        for (uint8 i; i < offchainRFP.projects.length; ) {
            Project storage project = rfp.projects[i];
            offchainRFP.projects[i].metadata = project.metadata;
            offchainRFP.projects[i].deadline = project.deadline;
            offchainRFP.projects[i].representative = project.representative;
            offchainRFP.projects[i].accepted = project.accepted;

            offchainRFP.projects[i].reward = new ITasks.Reward[](
                project.rewardCount
            );
            for (uint8 j; j < offchainRFP.projects[i].reward.length; ) {
                offchainRFP.projects[i].reward[j] = project.reward[j];
                unchecked {
                    ++j;
                }
            }
            offchainRFP.projects[i].nativeReward = new ITasks.NativeReward[](
                project.nativeRewardCount
            );

            for (uint8 j; j < offchainRFP.projects[i].nativeReward.length; ) {
                offchainRFP.projects[i].nativeReward[j] = project.nativeReward[
                    j
                ];
                unchecked {
                    ++j;
                }
            }
            unchecked {
                ++i;
            }
        }
    }

    // From: https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/proxy/Clones.sol
    function clone(address implementation) internal returns (address instance) {
        /// @solidity memory-safe-assembly
        assembly {
            // Cleans the upper 96 bits of the `implementation` word, then packs the first 3 bytes
            // of the `implementation` address with the bytecode before the address.
            mstore(
                0x00,
                or(
                    shr(0xe8, shl(0x60, implementation)),
                    0x3d602d80600a3d3981f3363d3d373d3d3d363d73000000
                )
            )
            // Packs the remaining 17 bytes of `implementation` with the bytecode after the address.
            mstore(
                0x20,
                or(shl(0x78, implementation), 0x5af43d82803e903d91602b57fd5bf3)
            )
            instance := create(0, 0x09, 0x37)
        }
        if (instance == address(0)) {
            revert ERC1167FailedCreateClone();
        }
    }

    function _toUint8(uint256 value) internal pure returns (uint8) {
        if (value > type(uint8).max) {
            revert Overflow();
        }
        return uint8(value);
    }

    function _toUint96(uint256 value) internal pure returns (uint96) {
        if (value > type(uint96).max) {
            revert Overflow();
        }
        return uint96(value);
    }
}

contracts/Tasks/ITasks.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Escrow} from "../Escrow.sol";

interface ITasks {
    error InvalidTimestamp();
    error InvalidAddress();
    error PointlessOperation();

    error TaskDoesNotExist();
    error TaskNotOpen();
    error TaskNotTaken();
    error TaskNotClosed();
    error TaskClosed();

    error NotManager();
    error NotExecutor();

    error RewardAboveBudget();
    error RewardDoesntEndWithNextToken();
    error IncorrectAmountOfNativeCurrencyAttached();
    error ApplicationDoesNotExist();
    error NotYourApplication();
    error ApplicationNotAccepted();
    error SubmissionDoesNotExist();
    error SubmissionAlreadyJudged();
    error JudgementNone();

    error RequestDoesNotExist();
    error RequestAlreadyAccepted();
    error RequestNotAccepted();
    error RequestAlreadyExecuted();

    error Overflow();
    error ManualBudgetIncreaseNeeded();
    error PartialRewardAboveFullReward();
    error NativeTransferFailed();
    error ERC1167FailedCreateClone();

    event TaskCreated(
        uint256 indexed taskId,
        string metadata,
        uint64 deadline,
        ERC20Transfer[] budget,
        uint96 nativeBudget,
        address creator,
        address manager
    );
    event ApplicationCreated(
        uint256 indexed taskId,
        uint16 applicationId,
        string metadata,
        Reward[] reward,
        NativeReward[] nativeReward
    );
    event ApplicationAccepted(uint256 indexed taskId, uint16 applicationId);
    event TaskTaken(uint256 indexed taskId, uint16 applicationId);
    event SubmissionCreated(
        uint256 indexed taskId,
        uint8 submissionId,
        string metadata
    );
    event SubmissionReviewed(
        uint256 indexed taskId,
        uint8 submissionId,
        SubmissionJudgement judgement,
        string feedback
    );
    event TaskCompleted(uint256 indexed taskId, TaskCompletion source);

    event CancelTaskRequested(
        uint256 indexed taskId,
        uint8 requestId,
        string explanation
    );
    event TaskCancelled(uint256 indexed taskId);
    event RequestAccepted(
        uint256 indexed taskId,
        RequestType requestType,
        uint8 requestId
    );
    event RequestExecuted(
        uint256 indexed taskId,
        RequestType requestType,
        uint8 requestId,
        address by
    );

    event DeadlineChanged(uint256 indexed taskId, uint64 newDeadline);
    event BudgetChanged(uint256 indexed taskId); // Quite expensive to transfer budget into a datastructure to emit
    event MetadataChanged(uint256 indexed taskId, string newMetadata);
    event PartialPayment(
        uint256 indexed taskId,
        uint88[] partialReward,
        uint96[] partialNativeReward
    );
    event NewManager(uint256 indexed taskId, address manager);

    /// @notice A container for ERC20 transfer information.
    /// @param tokenContract ERC20 token to transfer.
    /// @param amount How much of this token should be transfered.
    struct ERC20Transfer {
        IERC20 tokenContract;
        uint96 amount;
    }

    /// @notice A container for a reward payout.
    /// @param nextToken If this reward is payed out in the next ERC20 token.
    /// @dev IERC20 (address) is a lot of storage, rather just keep those only in budget.
    /// @notice nextToken should always be true for the last entry
    /// @param to Whom this token should be transfered to.
    /// @param amount How much of this token should be transfered.
    struct Reward {
        bool nextToken;
        address to;
        uint88 amount;
    }

    /// @notice A container for a native reward payout.
    /// @param to Whom the native reward should be transfered to.
    /// @param amount How much native reward should be transfered.
    struct NativeReward {
        address to;
        uint96 amount;
    }

    /// @notice A container for a task application.
    /// @param metadata Metadata of the application. (IPFS hash)
    /// @param applicant Who has submitted this application.
    /// @param accepted If the application has been accepted by the manager.
    /// @param reward How much rewards the applicant wants for completion.
    /// @param nativeReward How much native currency the applicant wants for completion.
    struct Application {
        string metadata;
        address applicant;
        bool accepted;
        uint8 rewardCount;
        uint8 nativeRewardCount;
        mapping(uint8 => Reward) reward;
        mapping(uint8 => NativeReward) nativeReward;
    }

    struct OffChainApplication {
        string metadata;
        address applicant;
        bool accepted;
        Reward[] reward;
        NativeReward[] nativeReward;
    }

    /// @notice For approving people on task creation (they are not required to make an application)
    struct PreapprovedApplication {
        address applicant;
        Reward[] reward;
        NativeReward[] nativeReward;
    }

    enum SubmissionJudgement {
        None,
        Accepted,
        Rejected
    }
    /// @notice A container for a task submission.
    /// @param metadata Metadata of the submission. (IPFS hash)
    /// @param judgement Judgement cast on the submission.
    /// @param feedback A response from the manager. (IPFS hash)
    struct Submission {
        string metadata;
        string feedback;
        SubmissionJudgement judgement;
    }

    enum RequestType {
        CancelTask
    }

    /// @notice A container for shared request information.
    /// @param accepted If the request was accepted.
    /// @param executed If the request was executed.
    struct Request {
        bool accepted;
        bool executed;
    }

    /// @notice A container for a request to cancel the task.
    /// @param request Request information.
    /// @param explanation Why the task should be cancelled.
    struct CancelTaskRequest {
        Request request;
        string explanation;
    }

    enum TaskState {
        Open,
        Taken,
        Closed
    }
    /// @notice A container for task-related information.
    /// @param metadata Metadata of the task. (IPFS hash)
    /// @param deadline Block timestamp at which the task expires if not completed.
    /// @param budget Maximum ERC20 rewards that can be earned by completing the task.
    /// @param nativeBudget Maximum native currency reward that can be earned by completing the task.
    /// @param creator Who has created the task.
    /// @param manager Who has the permission to manage the task.
    /// @param state Current state the task is in.
    /// @param applications Applications to take the job.
    /// @param executorApplication Index of the application that will execture the task.
    /// @param submissions Submission made to finish the task.
    struct Task {
        string metadata;
        // Storage block seperator
        uint64 deadline;
        Escrow escrow;
        // Storage block seperator
        uint96 nativeBudget;
        address creator;
        // Storage block seperator
        address manager;
        TaskState state;
        uint16 executorApplication;
        uint8 budgetCount;
        uint16 applicationCount;
        uint8 submissionCount;
        uint8 cancelTaskRequestCount;
        // Storage block seperator
        mapping(uint8 => ERC20Transfer) budget;
        mapping(uint16 => Application) applications;
        mapping(uint8 => Submission) submissions;
        mapping(uint8 => CancelTaskRequest) cancelTaskRequests;
    }

    struct OffChainTask {
        string metadata;
        uint64 deadline;
        uint16 executorApplication;
        address creator;
        address manager;
        TaskState state;
        Escrow escrow;
        uint96 nativeBudget;
        ERC20Transfer[] budget;
        OffChainApplication[] applications;
        Submission[] submissions;
        CancelTaskRequest[] cancelTaskRequests;
    }

    enum TaskCompletion {
        SubmissionAccepted,
        Dispute
    }

    /// @notice Retrieves the current amount of created tasks.
    function taskCount() external view returns (uint256);

    /// @notice Retrieves all task information by id.
    /// @param _taskId Id of the task.
    function getTask(
        uint256 _taskId
    ) external view returns (OffChainTask memory);

    /// @notice Retrieves multiple tasks.
    /// @param _taskIds Ids of the tasks.
    function getTasks(
        uint256[] calldata _taskIds
    ) external view returns (OffChainTask[] memory);

    /// @notice Create a new task.
    /// @param _metadata Metadata of the task. (IPFS hash)
    /// @param _deadline Block timestamp at which the task expires if not completed.
    /// @param _budget Maximum ERC20 rewards that can be earned by completing the task.
    /// @param _manager Who will manage the task (become the manager).
    /// @return taskId Id of the newly created task.
    function createTask(
        string calldata _metadata,
        uint64 _deadline,
        ERC20Transfer[] calldata _budget,
        address _manager,
        PreapprovedApplication[] calldata _preapprove
    ) external payable returns (uint256 taskId);

    /// @notice Apply to take the task.
    /// @param _taskId Id of the task.
    /// @param _metadata Metadata of your application.
    /// @param _reward Wanted rewards for completing the task.
    /// @param _nativeReward Wanted native currency for completing the task.
    /// @return applicationId Id of the newly created application.
    function applyForTask(
        uint256 _taskId,
        string calldata _metadata,
        Reward[] calldata _reward,
        NativeReward[] calldata _nativeReward
    ) external returns (uint16 applicationId);

    /// @notice Accept application to allow them to take the task.
    /// @param _taskId Id of the task.
    /// @param _applicationIds Indexes of the applications to accept.
    function acceptApplications(
        uint256 _taskId,
        uint16[] calldata _applicationIds
    ) external payable;

    /// @notice Take the task after your application has been accepted.
    /// @param _taskId Id of the task.
    /// @param _applicationId Index of application you made that has been accepted.
    function takeTask(uint256 _taskId, uint16 _applicationId) external;

    /// @notice Create a submission.
    /// @param _taskId Id of the task.
    /// @param _metadata Metadata of the submission. (IPFS hash)
    /// @return submissionId Id of the newly created submission.
    function createSubmission(
        uint256 _taskId,
        string calldata _metadata
    ) external returns (uint8 submissionId);

    /// @notice Review a submission.
    /// @param _taskId Id of the task.
    /// @param _submissionId Index of the submission that is reviewed.
    /// @param _judgement Outcome of the review.
    /// @param _feedback Reasoning of the reviewer. (IPFS hash)
    function reviewSubmission(
        uint256 _taskId,
        uint8 _submissionId,
        SubmissionJudgement _judgement,
        string calldata _feedback
    ) external;

    /// @notice Cancels a task. This can be used to close a task and receive back the budget.
    /// @param _taskId Id of the task.
    /// @param _explanation Why the task was cancelled. (IPFS hash)
    /// @return cancelTaskRequestId Id of the newly created request for task cancellation.
    function cancelTask(
        uint256 _taskId,
        string calldata _explanation
    ) external returns (uint8 cancelTaskRequestId);

    /// @notice Accepts a request, executing the proposed action.
    /// @param _taskId Id of the task.
    /// @param _requestType What kind of request it is.
    /// @param _requestId Id of the request.
    /// @param _execute If the request should also be executed in this transaction.
    function acceptRequest(
        uint256 _taskId,
        RequestType _requestType,
        uint8 _requestId,
        bool _execute
    ) external;

    /// @notice Exectued an accepted request, allows anyone to pay for the gas costs of the execution.
    /// @param _taskId Id of the task.
    /// @param _requestType What kind of request it is.
    /// @param _requestId Id of the request.
    function executeRequest(
        uint256 _taskId,
        RequestType _requestType,
        uint8 _requestId
    ) external;

    /// @notice Extend the deadline of a task.
    /// @param _taskId Id of the task.
    /// @param _extension How much to extend the deadline by.
    function extendDeadline(uint256 _taskId, uint64 _extension) external;

    /// @notice Increase the budget of the task.
    /// @param _taskId Id of the task.
    /// @param _increase How much to increase each tokens amount by.
    /// @dev Any attached native reward will also be used to increase the budget.
    function increaseBudget(
        uint256 _taskId,
        uint96[] calldata _increase
    ) external payable;

    /// @notice Edit the metadata of a task.
    /// @param _taskId Id of the task.
    /// @param _newMetadata New metadata of the task.
    /// @dev This metadata update might change the task completely. Show a warning to people who applied before the change.
    function editMetadata(
        uint256 _taskId,
        string calldata _newMetadata
    ) external;

    /// @notice Completes the task through dispute resolution.
    /// @param _taskId Id of the task.
    /// @param _partialReward How much of each ERC20 reward should be paid out.
    /// @param _partialNativeReward How much of each native reward should be paid out.
    function completeByDispute(
        uint256 _taskId,
        uint88[] calldata _partialReward,
        uint96[] calldata _partialNativeReward
    ) external;

    /// @notice Allows the dispute manager to appoint a new dispute manager.
    /// @param _newManager The new dispute manager.
    function transferDisputeManagement(address _newManager) external;

    /// @notice Releases a part of the reward to the executor without marking the task as complete.
    /// @param _taskId Id of the task.
    /// @param _partialReward How much of each ERC20 reward should be paid out.
    /// @param _partialNativeReward How much of each native reward should be paid out.
    /// @dev Will fetch balanceOf to set the budget afterwards, can be used in case funds where sent manually to increase the budget.
    function partialPayment(
        uint256 _taskId,
        uint88[] calldata _partialReward,
        uint96[] calldata _partialNativeReward
    ) external;

    /// @notice Transfers the manager role to a different address.
    /// @param _taskId Id of the task.
    /// @param _newManager What address should become the manager.
    function transferManagement(uint256 _taskId, address _newManager) external;
}

contracts/Tasks/Tasks.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {ITasks, IERC20, Escrow} from "./ITasks.sol";
import {TasksUtils} from "./TasksUtils.sol";

contract Tasks is TasksUtils {
    /// @notice The incremental ID for tasks.
    uint256 private taskCounter;

    /// @notice A mapping between task IDs and task information.
    mapping(uint256 => Task) internal tasks;

    /// @notice The base escrow contract that will be cloned for every task.
    address public immutable escrowImplementation;

    /// @notice This address has the power to disable the contract, in case an exploit is discovered.
    address private disabler;
    error Disabled();
    error NotDisabled();
    error NotDisabler();

    /// @notice This address has the power to handle disputes. It can complete any taken task without permission of the manager.
    /// @dev This should be a smart contract obviously.
    address public disputeManager;
    error NotDisputeManager();
    event NewDisputeManager(address disputeManager);

    constructor(address _disabler, address _disputeManager) {
        escrowImplementation = address(new Escrow());
        disabler = _disabler;
        disputeManager = _disputeManager;
        emit NewDisputeManager(_disputeManager);
    }

    /// @inheritdoc ITasks
    function taskCount() external view returns (uint256) {
        return taskCounter;
    }

    /// @inheritdoc ITasks
    function getTask(
        uint256 _taskId
    ) public view returns (OffChainTask memory offchainTask) {
        Task storage task = _getTask(_taskId);
        offchainTask = _toOffchainTask(task);
    }

    /// @inheritdoc ITasks
    function getTasks(
        uint256[] memory _taskIds
    ) public view returns (OffChainTask[] memory) {
        OffChainTask[] memory offchainTasks = new OffChainTask[](
            _taskIds.length
        );
        for (uint i; i < _taskIds.length; ) {
            offchainTasks[i] = getTask(_taskIds[i]);

            unchecked {
                ++i;
            }
        }
        return offchainTasks;
    }

    /// @inheritdoc ITasks
    function createTask(
        string calldata _metadata,
        uint64 _deadline,
        ERC20Transfer[] calldata _budget,
        address _manager,
        PreapprovedApplication[] calldata _preapprove
    ) external payable returns (uint256 taskId) {
        _ensureValidTimestamp(_deadline);
        _ensureValidAddress(_manager);

        _ensureNotDisabled();
        taskId = taskCounter++;

        Task storage task = tasks[taskId];
        task.metadata = _metadata;
        task.deadline = _deadline;
        Escrow escrow = Escrow(payable(clone(escrowImplementation)));
        escrow.__Escrow_init{value: msg.value}();
        task.escrow = escrow;
        // Gas optimization
        if (msg.value != 0) {
            task.nativeBudget = _toUint96(msg.value);
        }
        task.budgetCount = _toUint8(_budget.length);
        for (uint8 i; i < uint8(_budget.length); ) {
            _budget[i].tokenContract.transferFrom(
                msg.sender,
                address(escrow),
                _budget[i].amount
            );
            // use balanceOf in case there is a fee asoosiated with the transfer
            task.budget[i] = ERC20Transfer(
                _budget[i].tokenContract,
                _toUint96(_budget[i].tokenContract.balanceOf(address(escrow)))
            );
            unchecked {
                ++i;
            }
        }

        task.manager = _manager;
        task.creator = msg.sender;

        // Default values are already correct (save gas)
        // task.state = TaskState.Open;

        emit TaskCreated(
            taskId,
            _metadata,
            _deadline,
            _budget,
            _toUint96(msg.value),
            msg.sender,
            _manager
        );

        // Gas optimization
        if (_preapprove.length > 0) {
            task.applicationCount = _toUint16(_preapprove.length);
            for (uint16 i; i < uint16(_preapprove.length); ) {
                Application storage application = task.applications[i];
                application.applicant = _preapprove[i].applicant;
                application.accepted = true;
                _ensureRewardEndsWithNextToken(_preapprove[i].reward);
                _setRewardBellowBudget(
                    task,
                    application,
                    _preapprove[i].reward,
                    _preapprove[i].nativeReward
                );

                emit ApplicationCreated(
                    taskId,
                    i,
                    "",
                    _preapprove[i].reward,
                    _preapprove[i].nativeReward
                );

                emit ApplicationAccepted(taskId, i);

                unchecked {
                    ++i;
                }
            }
        }
    }

    /// @inheritdoc ITasks
    function applyForTask(
        uint256 _taskId,
        string calldata _metadata,
        Reward[] calldata _reward,
        NativeReward[] calldata _nativeReward
    ) external returns (uint16 applicationId) {
        _ensureNotDisabled();
        Task storage task = _getTask(_taskId);
        _ensureTaskIsOpen(task);
        _ensureRewardEndsWithNextToken(_reward);

        Application storage application = task.applications[
            task.applicationCount
        ];
        application.metadata = _metadata;
        application.applicant = msg.sender;

        // Gas optimization
        if (_reward.length != 0) {
            application.rewardCount = _toUint8(_reward.length);
            for (uint8 i; i < uint8(_reward.length); ) {
                application.reward[i] = _reward[i];
                unchecked {
                    ++i;
                }
            }
        }

        // Gas optimization
        if (_nativeReward.length != 0) {
            application.nativeRewardCount = _toUint8(_nativeReward.length);
            for (uint8 i; i < uint8(_nativeReward.length); ) {
                application.nativeReward[i] = _nativeReward[i];
                unchecked {
                    ++i;
                }
            }
        }

        applicationId = task.applicationCount++;

        emit ApplicationCreated(
            _taskId,
            applicationId,
            _metadata,
            _reward,
            _nativeReward
        );
    }

    /// @inheritdoc ITasks
    function acceptApplications(
        uint256 _taskId,
        uint16[] calldata _applicationIds
    ) external payable {
        _ensureNotDisabled();
        Task storage task = _getTask(_taskId);
        _ensureTaskIsOpen(task);
        _ensureSenderIsManager(task);

        for (uint i; i < _applicationIds.length; ) {
            _ensureApplicationExists(task, _applicationIds[i]);

            Application storage application = task.applications[
                _applicationIds[i]
            ];
            application.accepted = true;
            bool budgetIncreased = _increaseBudgetToReward(
                task,
                application.rewardCount,
                application.reward,
                application.nativeRewardCount,
                application.nativeReward
            );
            if (budgetIncreased) {
                emit BudgetChanged(_taskId);
            }
            emit ApplicationAccepted(_taskId, _applicationIds[i]);

            unchecked {
                ++i;
            }
        }
    }

    /// @inheritdoc ITasks
    function takeTask(uint256 _taskId, uint16 _applicationId) external {
        _ensureNotDisabled();
        Task storage task = _getTask(_taskId);
        _ensureTaskIsOpen(task);
        _ensureApplicationExists(task, _applicationId);

        Application storage application = task.applications[_applicationId];
        _ensureSenderIsApplicant(application);
        _ensureApplicationIsAccepted(application);

        task.executorApplication = _applicationId;
        task.state = TaskState.Taken;

        emit TaskTaken(_taskId, _applicationId);
    }

    /// @inheritdoc ITasks
    function createSubmission(
        uint256 _taskId,
        string calldata _metadata
    ) external returns (uint8 submissionId) {
        _ensureNotDisabled();
        Task storage task = _getTask(_taskId);
        _ensureTaskIsTaken(task);
        _ensureSenderIsExecutor(task);

        Submission storage submission = task.submissions[task.submissionCount];
        submission.metadata = _metadata;
        submissionId = task.submissionCount++;

        emit SubmissionCreated(_taskId, submissionId, _metadata);
    }

    /// @inheritdoc ITasks
    function reviewSubmission(
        uint256 _taskId,
        uint8 _submissionId,
        SubmissionJudgement _judgement,
        string calldata _feedback
    ) external {
        _ensureNotDisabled();
        Task storage task = _getTask(_taskId);
        _ensureTaskIsTaken(task);
        _ensureSenderIsManager(task);
        _ensureSubmissionExists(task, _submissionId);

        Submission storage submission = task.submissions[_submissionId];
        _ensureSubmissionNotJudged(submission);
        _ensureJudgementNotNone(_judgement);
        submission.judgement = _judgement;
        submission.feedback = _feedback;

        if (_judgement == SubmissionJudgement.Accepted) {
            _payoutTask(task);
            emit TaskCompleted(_taskId, TaskCompletion.SubmissionAccepted);
        }

        emit SubmissionReviewed(_taskId, _submissionId, _judgement, _feedback);
    }

    /// @inheritdoc ITasks
    function cancelTask(
        uint256 _taskId,
        string calldata _explanation
    ) external returns (uint8 cancelTaskRequestId) {
        _ensureNotDisabled();
        Task storage task = _getTask(_taskId);
        _ensureSenderIsManager(task);

        _ensureTaskNotClosed(task);

        if (
            task.state == TaskState.Open ||
            task.deadline <= uint64(block.timestamp)
        ) {
            // Task is open or deadline past
            _refundCreator(task);

            emit TaskCancelled(_taskId);

            // Max means no request
            cancelTaskRequestId = type(uint8).max;
        } else {
            // Task is taken and deadline has not past
            CancelTaskRequest storage request = task.cancelTaskRequests[
                task.cancelTaskRequestCount
            ];
            request.explanation = _explanation;
            cancelTaskRequestId = task.cancelTaskRequestCount++;

            emit CancelTaskRequested(
                _taskId,
                cancelTaskRequestId,
                _explanation
            );
        }
    }

    /// @inheritdoc ITasks
    function acceptRequest(
        uint256 _taskId,
        RequestType _requestType,
        uint8 _requestId,
        bool _execute
    ) external {
        _ensureNotDisabled();
        Task storage task = _getTask(_taskId);
        _ensureTaskIsTaken(task);
        _ensureSenderIsExecutor(task);

        //if (_requestType == RequestType.CancelTask) {
        {
            _ensureCancelTaskRequestExists(task, _requestId);

            CancelTaskRequest storage cancelTaskRequest = task
                .cancelTaskRequests[_requestId];
            _ensureRequestNotAccepted(cancelTaskRequest.request);

            if (_execute) {
                // use executeRequest in the body instead? (more gas due to all the checks, but less code duplication)
                _refundCreator(task);
                emit TaskCancelled(_taskId);

                cancelTaskRequest.request.executed = true;
            }

            cancelTaskRequest.request.accepted = true;
        }

        emit RequestAccepted(_taskId, _requestType, _requestId);
    }

    /// @inheritdoc ITasks
    function executeRequest(
        uint256 _taskId,
        RequestType _requestType,
        uint8 _requestId
    ) external {
        _ensureNotDisabled();
        Task storage task = _getTask(_taskId);
        _ensureTaskIsTaken(task);

        //if (_requestType == RequestType.CancelTask) {
        {
            _ensureCancelTaskRequestExists(task, _requestId);

            CancelTaskRequest storage cancelTaskRequest = task
                .cancelTaskRequests[_requestId];
            _ensureRequestAccepted(cancelTaskRequest.request);
            _ensureRequestNotExecuted(cancelTaskRequest.request);

            _refundCreator(task);

            emit TaskCancelled(_taskId);
            cancelTaskRequest.request.executed = true;
        }

        emit RequestExecuted(_taskId, _requestType, _requestId, msg.sender);
    }

    /// @inheritdoc ITasks
    function extendDeadline(uint256 _taskId, uint64 _extension) external {
        _ensureNotDisabled();
        Task storage task = _getTask(_taskId);
        _ensureSenderIsManager(task);

        _ensureTaskNotClosed(task);

        if (_extension == 0) {
            revert PointlessOperation();
        }
        task.deadline += _extension;

        emit DeadlineChanged(_taskId, task.deadline);
    }

    /// @inheritdoc ITasks
    function increaseBudget(
        uint256 _taskId,
        uint96[] calldata _increase
    ) external payable {
        _ensureNotDisabled();
        Task storage task = _getTask(_taskId);
        _ensureSenderIsManager(task);

        _ensureTaskIsOpen(task);

        for (uint8 i; i < uint8(_increase.length); ) {
            ERC20Transfer storage transfer = task.budget[i];
            transfer.tokenContract.transferFrom(
                msg.sender,
                address(task.escrow),
                _increase[i]
            );
            // Use balanceOf as there could be a fee in transferFrom

            transfer.amount = _toUint96(
                transfer.tokenContract.balanceOf(address(task.escrow))
            );

            unchecked {
                ++i;
            }
        }

        // Gas optimization
        if (msg.value != 0) {
            task.nativeBudget = _toUint96(task.nativeBudget + msg.value);
        }

        emit BudgetChanged(_taskId);
    }

    /// @inheritdoc ITasks
    function editMetadata(
        uint256 _taskId,
        string calldata _newMetadata
    ) external {
        _ensureNotDisabled();
        Task storage task = _getTask(_taskId);
        _ensureSenderIsManager(task);

        _ensureTaskIsOpen(task);

        if (equal(task.metadata, _newMetadata)) {
            revert PointlessOperation();
        }
        task.metadata = _newMetadata;
        emit MetadataChanged(_taskId, _newMetadata);
    }

    /// @inheritdoc ITasks
    function completeByDispute(
        uint256 _taskId,
        uint88[] calldata _partialReward,
        uint96[] calldata _partialNativeReward
    ) external {
        _ensureNotDisabled();
        Task storage task = _getTask(_taskId);
        _ensureSenderIsDisputeManager();

        _ensureTaskIsTaken(task);

        _payoutTaskPartially(task, _partialReward, _partialNativeReward);
        _refundCreator(task);

        emit PartialPayment(_taskId, _partialReward, _partialNativeReward);
        emit TaskCompleted(_taskId, TaskCompletion.Dispute);
    }

    /// @inheritdoc ITasks
    function transferDisputeManagement(address _newManager) external {
        _ensureSenderIsDisputeManager();
        disputeManager = _newManager;
        emit NewDisputeManager(_newManager);
    }

    /// @inheritdoc ITasks
    function partialPayment(
        uint256 _taskId,
        uint88[] calldata _partialReward,
        uint96[] calldata _partialNativeReward
    ) external {
        _ensureNotDisabled();
        Task storage task = _getTask(_taskId);
        _ensureSenderIsManager(task);

        _ensureTaskIsTaken(task);

        _payoutTaskPartially(task, _partialReward, _partialNativeReward);
        emit BudgetChanged(_taskId);
        emit PartialPayment(_taskId, _partialReward, _partialNativeReward);
    }

    /// @inheritdoc ITasks
    function transferManagement(uint256 _taskId, address _newManager) external {
        _ensureNotDisabled();
        Task storage task = _getTask(_taskId);
        _ensureSenderIsManager(task);

        _ensureTaskNotClosed(task);

        task.manager = _newManager;
        emit NewManager(_taskId, _newManager);
    }

    function disable() external {
        _ensureSenderIsDisabler();
        disabler = address(0);
    }

    // Ideally you are able to transfer it to the new contract, but that requires addition to the escrow contract
    // I prefer this, so the escrow contract keeps being basic (both for security and clone costs)
    function refund(uint256 _taskId) external {
        _ensureDisabled();
        Task storage task = _getTask(_taskId);
        _ensureTaskNotClosed(task);
        _refundCreator(task);
    }

    function _getTask(
        uint256 _taskId
    ) internal view returns (Task storage task) {
        if (_taskId >= taskCounter) {
            revert TaskDoesNotExist();
        }

        task = tasks[_taskId];
    }

    function _ensureSenderIsDisputeManager() internal view {
        if (msg.sender != disputeManager) {
            revert NotDisputeManager();
        }
    }

    function _ensureNotDisabled() internal view {
        if (disabler == address(0)) {
            revert Disabled();
        }
    }

    function _ensureDisabled() internal view {
        if (disabler != address(0)) {
            revert NotDisabled();
        }
    }

    function _ensureSenderIsDisabler() internal view {
        if (msg.sender != disabler) {
            revert NotDisabler();
        }
    }
}

contracts/Tasks/TasksEnsure.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {ITasks, Escrow} from "./ITasks.sol";

/*
  Functions to ensure a certain precondition is met.
*/
abstract contract TasksEnsure is ITasks {
    function _ensureTaskIsOpen(Task storage task) internal view {
        if (task.state != TaskState.Open) {
            revert TaskNotOpen();
        }
    }

    function _ensureTaskIsTaken(Task storage task) internal view {
        if (task.state != TaskState.Taken) {
            revert TaskNotTaken();
        }
    }

    function _ensureTaskNotClosed(Task storage task) internal view {
        if (task.state == TaskState.Closed) {
            revert TaskClosed();
        }
    }

    function _ensureSenderIsManager(Task storage task) internal view {
        if (task.manager != msg.sender) {
            revert NotManager();
        }
    }

    ///@dev Should only be called is the task is not open!
    function _ensureSenderIsExecutor(Task storage task) internal view {
        if (
            task.applications[task.executorApplication].applicant != msg.sender
        ) {
            revert NotExecutor();
        }
    }

    function _ensureRewardEndsWithNextToken(
        Reward[] memory reward
    ) internal pure {
        unchecked {
            if (reward.length != 0 && !reward[reward.length - 1].nextToken) {
                revert RewardDoesntEndWithNextToken();
            }
        }
    }

    function _ensureApplicationExists(
        Task storage task,
        uint16 _applicationId
    ) internal view {
        if (_applicationId >= task.applicationCount) {
            revert ApplicationDoesNotExist();
        }
    }

    function _ensureSenderIsApplicant(
        Application storage application
    ) internal view {
        if (application.applicant != msg.sender) {
            revert NotYourApplication();
        }
    }

    function _ensureApplicationIsAccepted(
        Application storage application
    ) internal view {
        if (!application.accepted) {
            revert ApplicationNotAccepted();
        }
    }

    function _ensureSubmissionExists(
        Task storage task,
        uint8 _submissionId
    ) internal view {
        if (_submissionId >= task.submissionCount) {
            revert SubmissionDoesNotExist();
        }
    }

    function _ensureSubmissionNotJudged(
        Submission storage submission
    ) internal view {
        if (submission.judgement != SubmissionJudgement.None) {
            revert SubmissionAlreadyJudged();
        }
    }

    function _ensureJudgementNotNone(
        SubmissionJudgement judgement
    ) internal pure {
        if (judgement == SubmissionJudgement.None) {
            revert JudgementNone();
        }
    }

    function _ensureCancelTaskRequestExists(
        Task storage task,
        uint8 _requestId
    ) internal view {
        if (_requestId >= task.cancelTaskRequestCount) {
            revert RequestDoesNotExist();
        }
    }

    function _ensureRequestNotAccepted(Request storage request) internal view {
        if (request.accepted) {
            revert RequestAlreadyAccepted();
        }
    }

    function _ensureRequestAccepted(Request storage request) internal view {
        if (!request.accepted) {
            revert RequestNotAccepted();
        }
    }

    function _ensureRequestNotExecuted(Request storage request) internal view {
        if (request.executed) {
            revert RequestAlreadyExecuted();
        }
    }

    function _ensureValidTimestamp(uint64 timestamp) internal pure {
        if (timestamp == 0) {
            revert InvalidTimestamp();
        }
    }

    function _ensureValidAddress(address addr) internal pure {
        if (addr == address(0)) {
            revert InvalidAddress();
        }
    }

    function _toUint8(uint256 value) internal pure returns (uint8) {
        if (value > type(uint8).max) {
            revert Overflow();
        }
        return uint8(value);
    }

    function _toUint16(uint256 value) internal pure returns (uint16) {
        if (value > type(uint16).max) {
            revert Overflow();
        }
        return uint16(value);
    }

    function _toUint96(uint256 value) internal pure returns (uint96) {
        if (value > type(uint96).max) {
            revert Overflow();
        }
        return uint96(value);
    }
}

contracts/Tasks/TasksUtils.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {Escrow, TasksEnsure} from "./TasksEnsure.sol";

/*
  Higher level functions to allow the Tasks file to be more readable.
*/
abstract contract TasksUtils is TasksEnsure {
    function _toOffchainTask(
        Task storage task
    ) internal view returns (OffChainTask memory offchainTask) {
        offchainTask.metadata = task.metadata;
        offchainTask.deadline = task.deadline;
        offchainTask.executorApplication = task.executorApplication;
        offchainTask.creator = task.creator;
        offchainTask.manager = task.manager;
        offchainTask.state = task.state;
        offchainTask.escrow = task.escrow;
        offchainTask.nativeBudget = task.nativeBudget;

        offchainTask.budget = new ERC20Transfer[](task.budgetCount);
        for (uint8 i; i < offchainTask.budget.length; ) {
            offchainTask.budget[i] = task.budget[i];
            unchecked {
                ++i;
            }
        }

        offchainTask.applications = new OffChainApplication[](
            task.applicationCount
        );
        for (uint8 i; i < offchainTask.applications.length; ) {
            Application storage application = task.applications[i];
            offchainTask.applications[i].metadata = application.metadata;
            offchainTask.applications[i].applicant = application.applicant;
            offchainTask.applications[i].accepted = application.accepted;

            offchainTask.applications[i].reward = new Reward[](
                application.rewardCount
            );
            for (uint8 j; j < offchainTask.applications[i].reward.length; ) {
                offchainTask.applications[i].reward[j] = application.reward[j];
                unchecked {
                    ++j;
                }
            }
            offchainTask.applications[i].nativeReward = new NativeReward[](
                application.nativeRewardCount
            );

            for (
                uint8 j;
                j < offchainTask.applications[i].nativeReward.length;

            ) {
                offchainTask.applications[i].nativeReward[j] = application
                    .nativeReward[j];
                unchecked {
                    ++j;
                }
            }
            unchecked {
                ++i;
            }
        }

        offchainTask.submissions = new Submission[](task.submissionCount);
        for (uint8 i; i < offchainTask.submissions.length; ) {
            offchainTask.submissions[i] = task.submissions[i];
            unchecked {
                ++i;
            }
        }

        offchainTask.cancelTaskRequests = new CancelTaskRequest[](
            task.cancelTaskRequestCount
        );
        for (uint8 i; i < offchainTask.cancelTaskRequests.length; ) {
            offchainTask.cancelTaskRequests[i] = task.cancelTaskRequests[i];
            unchecked {
                ++i;
            }
        }
    }

    function _increaseBudgetToReward(
        Task storage task,
        uint8 _length,
        mapping(uint8 => Reward) storage _reward,
        uint8 _nativeLength,
        mapping(uint8 => NativeReward) storage _nativeReward
    ) internal returns (bool increasedBudget) {
        // Gas optimzation
        if (_length != 0) {
            uint8 j;
            ERC20Transfer memory erc20Transfer = task.budget[0];
            uint256 needed;
            for (uint8 i; i < _length; ) {
                unchecked {
                    needed += _reward[i].amount;
                }

                if (_reward[i].nextToken) {
                    if (needed > erc20Transfer.amount) {
                        // Existing budget in escrow doesnt cover the needed reward
                        erc20Transfer.tokenContract.transferFrom(
                            msg.sender,
                            address(task.escrow),
                            needed - erc20Transfer.amount
                        );

                        uint256 got = erc20Transfer.tokenContract.balanceOf(
                            address(task.escrow)
                        );

                        if (got < needed) {
                            // Apparently there is a tax / fee on the token transfer
                            revert ManualBudgetIncreaseNeeded();
                        }

                        task.budget[j].amount = _toUint96(got);
                        increasedBudget = true;
                    }

                    needed = 0;
                    unchecked {
                        erc20Transfer = task.budget[++j];
                    }
                }

                unchecked {
                    ++i;
                }
            }
        }

        if (_nativeLength != 0) {
            uint256 nativeNeeded;
            for (uint8 i; i < _nativeLength; ) {
                nativeNeeded += _nativeReward[i].amount;
                unchecked {
                    ++i;
                }
            }

            if (nativeNeeded > task.nativeBudget) {
                unchecked {
                    if (msg.value != nativeNeeded - task.nativeBudget) {
                        revert IncorrectAmountOfNativeCurrencyAttached();
                    }
                }

                (bool success, ) = address(task.escrow).call{value: msg.value}(
                    ""
                );
                if (!success) {
                    revert NativeTransferFailed();
                }

                task.nativeBudget = _toUint96(nativeNeeded);
                increasedBudget = true;
            }
        }
    }

    function _setRewardBellowBudget(
        Task storage task,
        Application storage application,
        Reward[] calldata _reward,
        NativeReward[] calldata _nativeReward
    ) internal {
        // Gas optimzation
        if (_reward.length != 0) {
            application.rewardCount = _toUint8(_reward.length);

            uint8 j;
            ERC20Transfer memory erc20Transfer = task.budget[0];
            uint256 alreadyReserved;
            for (uint8 i; i < uint8(_reward.length); ) {
                unchecked {
                    alreadyReserved += _reward[i].amount;
                }

                application.reward[i] = _reward[i];

                if (_reward[i].nextToken) {
                    if (alreadyReserved > erc20Transfer.amount) {
                        revert RewardAboveBudget();
                    }
                    alreadyReserved = 0;
                    unchecked {
                        erc20Transfer = task.budget[++j];
                    }
                }

                unchecked {
                    ++i;
                }
            }
        }

        // Gas optimzation
        if (_nativeReward.length != 0) {
            application.nativeRewardCount = _toUint8(_nativeReward.length);
            uint256 nativeReserved;
            for (uint8 i; i < uint8(_nativeReward.length); ) {
                unchecked {
                    nativeReserved += _nativeReward[i].amount;
                }

                application.nativeReward[i] = _nativeReward[i];

                unchecked {
                    ++i;
                }
            }

            if (nativeReserved > task.nativeBudget) {
                revert RewardAboveBudget();
            }
        }
    }

    function _payoutTask(Task storage task) internal {
        Application storage executor = task.applications[
            task.executorApplication
        ];
        address creator = task.creator;
        Escrow escrow = task.escrow;

        // Gas optimzation
        uint8 rewardCount = executor.rewardCount;
        if (rewardCount != 0) {
            uint8 j;
            uint8 budgetCount = task.budgetCount;
            for (uint8 i; i < budgetCount; ) {
                ERC20Transfer memory erc20Transfer = task.budget[i];
                while (j < rewardCount) {
                    Reward memory reward = executor.reward[j];
                    escrow.transfer(
                        erc20Transfer.tokenContract,
                        reward.to,
                        reward.amount
                    );

                    unchecked {
                        erc20Transfer.amount -= reward.amount;
                        ++j;
                    }

                    if (reward.nextToken) {
                        break;
                    }
                }

                // Gas optimization
                if (erc20Transfer.amount != 0) {
                    escrow.transfer(
                        erc20Transfer.tokenContract,
                        creator,
                        erc20Transfer.amount
                    );
                }

                unchecked {
                    ++i;
                }
            }
        }

        // Gas optimzation
        uint8 nativeRewardCount = executor.nativeRewardCount;
        if (nativeRewardCount != 0) {
            uint96 paidOut;
            for (uint8 i; i < nativeRewardCount; ) {
                escrow.transferNative(
                    payable(executor.nativeReward[i].to),
                    executor.nativeReward[i].amount
                );
                unchecked {
                    paidOut += executor.nativeReward[i].amount;
                }

                unchecked {
                    ++i;
                }
            }

            // Gas optimzation
            if (paidOut < task.nativeBudget) {
                unchecked {
                    escrow.transferNative(
                        payable(task.creator),
                        task.nativeBudget - paidOut
                    );
                }
            }
        }

        task.state = TaskState.Closed;
    }

    function _refundCreator(Task storage task) internal {
        Escrow escrow = task.escrow;
        address creator = task.creator;

        uint8 budgetCount = task.budgetCount;
        if (budgetCount != 0) {
            for (uint8 i; i < budgetCount; ) {
                ERC20Transfer memory erc20Transfer = task.budget[i];
                escrow.transfer(
                    erc20Transfer.tokenContract,
                    creator,
                    erc20Transfer.amount
                );

                unchecked {
                    ++i;
                }
            }
        }

        // Gas optimzation
        if (task.nativeBudget != 0) {
            escrow.transferNative(payable(creator), task.nativeBudget);
        }

        task.state = TaskState.Closed;
    }

    function _payoutTaskPartially(
        Task storage task,
        uint88[] calldata _partialReward,
        uint96[] calldata _partialNativeReward
    ) internal {
        Application storage executor = task.applications[
            task.executorApplication
        ];
        Escrow escrow = task.escrow;

        // Gas optimzation
        uint8 rewardCount = executor.rewardCount;
        if (rewardCount != 0) {
            uint8 j;
            uint8 budgetCount = task.budgetCount;
            for (uint8 i; i < budgetCount; ) {
                ERC20Transfer memory erc20Transfer = task.budget[i];
                while (j < rewardCount) {
                    Reward memory reward = executor.reward[j];
                    if (_partialReward[j] > reward.amount) {
                        revert PartialRewardAboveFullReward();
                    }

                    escrow.transfer(
                        erc20Transfer.tokenContract,
                        reward.to,
                        _partialReward[j]
                    );

                    unchecked {
                        executor.reward[j].amount =
                            reward.amount -
                            _partialReward[j];

                        ++j;
                    }

                    if (reward.nextToken) {
                        break;
                    }
                }

                task.budget[i].amount = _toUint96(
                    erc20Transfer.tokenContract.balanceOf(address(escrow))
                );

                unchecked {
                    ++i;
                }
            }
        }

        // Gas optimzation
        uint8 nativeRewardCount = executor.nativeRewardCount;
        if (nativeRewardCount != 0) {
            for (uint8 i; i < nativeRewardCount; ) {
                if (_partialNativeReward[i] > executor.nativeReward[i].amount) {
                    revert PartialRewardAboveFullReward();
                }

                escrow.transferNative(
                    payable(executor.nativeReward[i].to),
                    _partialNativeReward[i]
                );

                unchecked {
                    executor.nativeReward[i].amount -= _partialNativeReward[i];
                    ++i;
                }
            }

            unchecked {
                task.nativeBudget = _toUint96(address(escrow).balance);
            }
        }
    }

    // From: https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/proxy/Clones.sol
    function clone(address implementation) internal returns (address instance) {
        /// @solidity memory-safe-assembly
        assembly {
            // Cleans the upper 96 bits of the `implementation` word, then packs the first 3 bytes
            // of the `implementation` address with the bytecode before the address.
            mstore(
                0x00,
                or(
                    shr(0xe8, shl(0x60, implementation)),
                    0x3d602d80600a3d3981f3363d3d373d3d3d363d73000000
                )
            )
            // Packs the remaining 17 bytes of `implementation` with the bytecode after the address.
            mstore(
                0x20,
                or(shl(0x78, implementation), 0x5af43d82803e903d91602b57fd5bf3)
            )
            instance := create(0, 0x09, 0x37)
        }
        if (instance == address(0)) {
            revert ERC1167FailedCreateClone();
        }
    }

    // From: https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/utils/Strings.sol
    function equal(
        string memory a,
        string memory b
    ) internal pure returns (bool) {
        return keccak256(bytes(a)) == keccak256(bytes(b));
    }
}

