// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {IDAO} from "@aragon/osx/core/dao/IDAO.sol";

bytes32 constant GRANT_ACCESS_PERMISSION_ID = keccak256(
    "GRANT_ACCESS_PERMISSION"
);
bytes32 constant REVOKE_ACCESS_PERMISSION_ID = keccak256(
    "REVOKE_ACCESS_PERMISSION"
);

interface ISharedAddress {
    error NotWearingHat();
    error AccessDenied();

    event Execution(
        uint256 indexed nonce,
        address indexed sender,
        uint256 hat,
        IDAO.Action[] actions
    );

    event FullAccessGranted(uint256 indexed hat);
    event FullZoneAccessGranted(uint256 indexed hat, address zone);
    event FullFunctionAccessGranted(
        uint256 indexed hat,
        address zone,
        bytes4 functionSelector
    );

    event FullAccessRevoked(uint256 indexed hat);
    event FullZoneAccessRevoked(uint256 indexed hat, address zone);
    event FullFunctionAccessRevoked(
        uint256 indexed hat,
        address zone,
        bytes4 functionSelector
    );

    /// @notice Verifies if a hat is allowed to execute a list of actions.
    /// @param _hat The hat to check with.
    /// @param _actions The actions to check.
    /// @dev Only a single hat means that a user holding multiple hats might need to split their actions into one batch per hat.
    function hasAccess(
        uint256 _hat,
        IDAO.Action[] calldata _actions
    ) external view returns (bool);

    /// @notice Executes a list of actions as the DAO.
    /// @param _hat The hat to use for validations the actions are allowed.
    /// @param _actions The actions to execute.
    /// @param _failureMap Which actions are allowed to fail without reverting the whole transaction.
    function asDAO(
        uint256 _hat,
        IDAO.Action[] calldata _actions,
        uint256 _failureMap
    ) external returns (bytes[] memory, uint256);

    /// @notice Grants a hat the permission to do any action.
    /// @param _hat The hat that is granted the permission.
    function grantFullAccess(uint256 _hat) external;

    /// @notice Grants a hat the permission to call all functions of one smart contract.
    /// @param _hat The hat that is granted the permission.
    /// @param _zone The address of the smart contract.
    function grantFullZoneAccess(uint256 _hat, address _zone) external;

    /// @notice Grants a hat the permission to call one function of one smart contract.
    /// @param _hat The hat that is granted the permission.
    /// @param _zone The address of the smart contract.
    /// @param _function The function of the smart contract.
    function grantFullFunctionAccess(
        uint256 _hat,
        address _zone,
        bytes4 _function
    ) external;

    /// @notice Revokes a hat the permission to do any action.
    /// @param _hat The hat that is granted the permission.
    function revokeFullAccess(uint256 _hat) external;

    /// @notice Revokes a hat the permission to call all functions of one smart contract.
    /// @param _hat The hat that is granted the permission.
    /// @param _zone The address of the smart contract.
    function revokeFullZoneAccess(uint256 _hat, address _zone) external;

    /// @notice Revokes a hat the permission to call one function of one smart contract.
    /// @param _hat The hat that is granted the permission.
    /// @param _zone The address of the smart contract.
    /// @param _function The function of the smart contract.
    function revokeFullFunctionAccess(
        uint256 _hat,
        address _zone,
        bytes4 _function
    ) external;
}

contracts/DAO/SharedAddress/SharedAddres.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17; // For verification, the setup contract (deployer) is fixed on 0.8.17

import {PluginUUPSUpgradeable} from "@aragon/osx/core/plugin/PluginUUPSUpgradeable.sol";
import {IHats} from "../../Deps/hats-protocol/Interfaces/IHats.sol";
import {ISharedAddress, IDAO, GRANT_ACCESS_PERMISSION_ID, REVOKE_ACCESS_PERMISSION_ID} from "./ISharedAddress.sol";

contract SharedAddress is PluginUUPSUpgradeable, ISharedAddress {
    uint256 private daoNonce;
    IHats private hats; // Should work for any ERC-1155 collection though
    mapping(uint256 => bool) private fullAccess;
    mapping(address => mapping(uint256 => bool)) private fullZoneAccess;
    mapping(uint256 => mapping(uint256 => bool)) private fullFunctionAccess;

    modifier wearingHat(uint256 _hat) {
        if (hats.balanceOf(msg.sender, _hat) == 0) {
            revert NotWearingHat();
        }
        _;
    }

    /// @notice Initialize the TaskDisputes plugin.
    /// @param _dao The dao where this plugin is installed.
    /// @param _hats The deployment of Hats Protocol to use.
    function initialize(IDAO _dao, IHats _hats) external initializer {
        __PluginUUPSUpgradeable_init(_dao);
        hats = _hats;
    }

    /// @inheritdoc PluginUUPSUpgradeable
    function supportsInterface(
        bytes4 _interfaceId
    ) public view virtual override returns (bool) {
        return
            _interfaceId == type(ISharedAddress).interfaceId ||
            super.supportsInterface(_interfaceId);
    }

    /// @inheritdoc ISharedAddress
    function hasAccess(
        uint256 _hat,
        IDAO.Action[] calldata _actions
    ) public view returns (bool) {
        if (!fullAccess[_hat]) {
            for (uint i; i < _actions.length; ) {
                if (!_hasAccess(_hat, _actions[i])) {
                    return false;
                }

                unchecked {
                    ++i;
                }
            }
        }
        return true;
    }

    /// @inheritdoc ISharedAddress
    function asDAO(
        uint256 _hat,
        IDAO.Action[] calldata _actions,
        uint256 _failureMap
    )
        external
        wearingHat(_hat)
        returns (bytes[] memory returnValueBytes, uint256 failureMap)
    {
        if (!hasAccess(_hat, _actions)) {
            revert AccessDenied();
        }

        (returnValueBytes, failureMap) = dao().execute(
            bytes32(daoNonce),
            _actions,
            _failureMap
        );
        emit Execution(daoNonce, msg.sender, _hat, _actions);
        ++daoNonce;
    }

    /// @inheritdoc ISharedAddress
    function grantFullAccess(
        uint256 _hat
    ) external auth(GRANT_ACCESS_PERMISSION_ID) {
        fullAccess[_hat] = true;
        emit FullAccessGranted(_hat);
    }

    /// @inheritdoc ISharedAddress
    function grantFullZoneAccess(
        uint256 _hat,
        address _zone
    ) external auth(GRANT_ACCESS_PERMISSION_ID) {
        fullZoneAccess[_zone][_hat] = true;
        emit FullZoneAccessGranted(_hat, _zone);
    }

    /// @inheritdoc ISharedAddress
    function grantFullFunctionAccess(
        uint256 _hat,
        address _zone,
        bytes4 _function
    ) external auth(GRANT_ACCESS_PERMISSION_ID) {
        fullFunctionAccess[_functionId(_zone, _function)][_hat] = true;
        emit FullFunctionAccessGranted(_hat, _zone, _function);
    }

    /// @inheritdoc ISharedAddress
    function revokeFullAccess(
        uint256 _hat
    ) external auth(REVOKE_ACCESS_PERMISSION_ID) {
        fullAccess[_hat] = false;
        emit FullAccessRevoked(_hat);
    }

    /// @inheritdoc ISharedAddress
    function revokeFullZoneAccess(
        uint256 _hat,
        address _zone
    ) external auth(REVOKE_ACCESS_PERMISSION_ID) {
        fullZoneAccess[_zone][_hat] = false;
        emit FullZoneAccessRevoked(_hat, _zone);
    }

    /// @inheritdoc ISharedAddress
    function revokeFullFunctionAccess(
        uint256 _hat,
        address _zone,
        bytes4 _function
    ) external auth(REVOKE_ACCESS_PERMISSION_ID) {
        fullFunctionAccess[_functionId(_zone, _function)][_hat] = false;
        emit FullFunctionAccessRevoked(_hat, _zone, _function);
    }

    function _hasAccess(
        uint256 _hat,
        IDAO.Action calldata _action
    ) internal view returns (bool) {
        return
            fullZoneAccess[_action.to][_hat] ||
            fullFunctionAccess[_functionId(_action.to, bytes4(_action.data))][
                _hat
            ];
    }

    // address + function selector
    function _functionId(
        address _zone,
        bytes4 _function
    ) internal pure returns (uint256) {
        return (uint160(bytes20(_zone)) << 32) + uint32(_function);
    }

    /// @dev This empty reserved space is put in place to allow future versions to add new
    /// variables without shifting down storage in the inheritance chain.
    /// https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
    uint256[50] private __gap;
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {IDAO} from "@aragon/osx/core/dao/IDAO.sol";
import {DAO} from "@aragon/osx/core/dao/DAO.sol";
import {PermissionLib} from "@aragon/osx/core/permission/PermissionLib.sol";
import {PluginSetup, IPluginSetup} from "@aragon/osx/framework/plugin/setup/PluginSetup.sol";
import {SharedAddress, IHats, GRANT_ACCESS_PERMISSION_ID, REVOKE_ACCESS_PERMISSION_ID} from "./SharedAddress.sol";

contract SharedAddressSetup is PluginSetup {
    /// @notice The address of `SharedAddress` plugin logic contract to be used in creating proxy contracts.
    SharedAddress private immutable sharedAddressBase;

    /// @notice The contract constructor, that deploys the `SharedAddress` plugin logic contract.
    constructor() {
        sharedAddressBase = new SharedAddress();
    }

    /// @inheritdoc IPluginSetup
    function prepareInstallation(
        address _dao,
        bytes calldata _data
    )
        external
        returns (address plugin, PreparedSetupData memory preparedSetupData)
    {
        // Decode `_data` to extract the params needed for deploying and initializing `SharedAddress` plugin.
        IHats hats = abi.decode(_data, (IHats));

        // Prepare and Deploy the plugin proxy.
        plugin = createERC1967Proxy(
            address(sharedAddressBase),
            abi.encodeWithSelector(
                SharedAddress.initialize.selector,
                _dao,
                hats
            )
        );

        // Prepare permissions
        PermissionLib.MultiTargetPermission[]
            memory permissions = new PermissionLib.MultiTargetPermission[](3);

        // Set permissions to be granted.
        // Grant the list of permissions of the plugin to the DAO.
        permissions[0] = PermissionLib.MultiTargetPermission(
            PermissionLib.Operation.Grant,
            plugin,
            _dao,
            PermissionLib.NO_CONDITION,
            GRANT_ACCESS_PERMISSION_ID
        );

        permissions[1] = PermissionLib.MultiTargetPermission(
            PermissionLib.Operation.Grant,
            plugin,
            _dao,
            PermissionLib.NO_CONDITION,
            REVOKE_ACCESS_PERMISSION_ID
        );

        // Grant `EXECUTE_PERMISSION` of the DAO to the plugin.
        permissions[2] = PermissionLib.MultiTargetPermission(
            PermissionLib.Operation.Grant,
            _dao,
            plugin,
            PermissionLib.NO_CONDITION,
            DAO(payable(_dao)).EXECUTE_PERMISSION_ID()
        );

        preparedSetupData.permissions = permissions;
    }

    /// @inheritdoc IPluginSetup
    function prepareUninstallation(
        address _dao,
        SetupPayload calldata _payload
    )
        external
        view
        returns (PermissionLib.MultiTargetPermission[] memory permissions)
    {
        // Prepare permissions
        permissions = new PermissionLib.MultiTargetPermission[](1);

        // Set permissions to be Revoked.
        permissions[0] = PermissionLib.MultiTargetPermission(
            PermissionLib.Operation.Revoke,
            _payload.plugin,
            _dao,
            PermissionLib.NO_CONDITION,
            GRANT_ACCESS_PERMISSION_ID
        );

        permissions[1] = PermissionLib.MultiTargetPermission(
            PermissionLib.Operation.Revoke,
            _payload.plugin,
            _dao,
            PermissionLib.NO_CONDITION,
            REVOKE_ACCESS_PERMISSION_ID
        );

        permissions[2] = PermissionLib.MultiTargetPermission(
            PermissionLib.Operation.Revoke,
            _dao,
            _payload.plugin,
            PermissionLib.NO_CONDITION,
            DAO(payable(_dao)).EXECUTE_PERMISSION_ID()
        );
    }

    /// @inheritdoc IPluginSetup
    function implementation() external view returns (address) {
        return address(sharedAddressBase);
    }
}

